CREATE TABLE IF NOT EXISTS `1668940036_wp_zbs_quotes_templates` ( `ID` int(11) NOT NULL AUTO_INCREMENT, `zbs_site` int(11) DEFAULT NULL, `zbs_team` int(11) DEFAULT NULL, `zbs_owner` int(11) NOT NULL, `zbsqt_title` varchar(255) DEFAULT NULL, `zbsqt_value` decimal(18,2) DEFAULT '0.00', `zbsqt_date_str` varchar(20) DEFAULT NULL, `zbsqt_date` int(14) DEFAULT NULL, `zbsqt_content` longtext, `zbsqt_notes` longtext, `zbsqt_currency` varchar(4) NOT NULL DEFAULT '-1', `zbsqt_created` int(14) NOT NULL, `zbsqt_lastupdated` int(14) NOT NULL, PRIMARY KEY (`ID`), KEY `title` (`zbsqt_title`), KEY `created` (`zbsqt_created`)) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;
INSERT INTO `1668940036_wp_zbs_quotes_templates` (`ID`, `zbs_site`, `zbs_team`, `zbs_owner`, `zbsqt_title`, `zbsqt_value`, `zbsqt_date_str`, `zbsqt_date`, `zbsqt_content`, `zbsqt_notes`, `zbsqt_currency`, `zbsqt_created`, `zbsqt_lastupdated`) VALUES (1,1,1,5,'Web Design: Example',500,'',0,'<h2>Summary</h2>\n<p>A paragraph summary, such as: Works to website at clientsdomain.com, including a re-design with three client-feedback stages, and translation into French.</p>\n<h2>Strategic Goals</h2>\n<p>##BIZ-NAME##’s experience is your secret advantage. We’re super aware of what’s necessary to present a strong online presence in 2017. We’ve been doing this for more than a decade, and the number one reason our clients come back time and time again is that we know that your strategic goals are our strategic goals.</p>\n<p>At ##CUSTOMERNAME##, you’ve told us that you need to refine your online experience, and&nbsp;to improve engagement. We will&nbsp;deliver&nbsp;on these goals, and have&nbsp;applied our years of practical experience, and broken these down into deliverables, each marking a stage of completion where you can feedback. We’ve also analysed your requests and come up with some concrete markers which we can use to ensure we over deliver!:</p>\n<ul>\n    <li>Refine online experience @ clientsdomain.com\n        <ul>\n            <li>Marker: User Experience feedback score before and after work, with a 30% improvement overall</li>\n        </ul>\n    </li>\n    <li>Improve engagement.\n        <ul>\n            <li>Marker: Bail-rate reduced by 50%</li>\n        </ul>\n    </li>\n</ul>\n<h2>Deliverables</h2>\n<p>A strong online presence is fundamental to&nbsp;##CUSTOMERNAME##’s business, so we’re suggesting this work take place over three stages, with a trial run, or soft-launch, to protect your brand.</p>\n<ol>\n    <li>First-fix of new design delivered to you and your team for feedback (2 week turnaround)</li>\n    <li>Feedback acted-upon, design re-presented to larger stakeholder group (2 week turnaround)</li>\n    <li>Soft-launch to 25% of traffic to your main website (2 week turnaround)</li>\n    <li>Final handover to your in-house developer</li>\n</ol>\n<h2>Quotation</h2>\n<table class=\"table table-striped\">\n    <tbody>\n        <tr>\n            <td><b>Item</b></td>\n            <td><b>Cost</b></td>\n        </tr>\n        <tr>\n            <td>Item 1</td>\n            <td>$0.00</td>\n        </tr>\n        <tr>\n            <td>Item 2</td>\n            <td>$0.00</td>\n        </tr>\n        <tr>\n            <td>##QUOTE-TITLE## Total</td>\n            <td>##QUOTEVALUE##</td>\n        </tr>\n    </tbody>\n</table>\n<p>(To be paid as follows…)</p>\n<ul>\n    <li>Deposit: 40%</li>\n    <li>Stage 1 payment (on delivery of deliverable 1): 10%</li>\n    <li>Stage 2 payment (on delivery of deliverable 2): 20%</li>\n    <li>Final payment (balance, on completion of deliverables): 30%</li>\n</ul>\n<h2>Getting Started</h2>\n<p>This quotation, dated&nbsp;##QUOTEDATE##, is available for immediate commencement. When you are ready to proceed, please do accept below by entering your email address and hitting accept.</p>\n<p>If you would like an amendment, or to discuss details,&nbsp;please do let&nbsp;us know via email (your@email.com), or phone (01234 567 891).</p>\n<p>Once accepted, ##BIZ-NAME## will schedule a commencement meeting with ##CUSTOMERNAME##, check over the initial details, and then proceed with the works as stated above.</p>\n<h2>Terms and Conditions</h2>\n<p><strong>NOTE:</strong> We recommend that you write these yourself, as these are your legal protection in any dispute with your client. It is worth firming up dates, and client components of your “deliverables” here – e.g. if they need to send you a logo before you can finish stage 1, write a clause which says “we need a high-res logo by date X, otherwise the stage will be classed as complete”.</p>','','',1668185855,1668185855);
